--// UI PHUCMAX + FPS + Ping (Rainbow Gradient rõ sáng)local ScreenGui = Instance.new("ScreenGui")ScreenGui.Name = "PHUCMAX_UI"ScreenGui.ResetOnSpawn = falseScreenGui.Parent = game.CoreGui-- Frame chứa textlocal Frame = Instance.new("Frame")Frame.Parent = ScreenGuiFrame.BackgroundTransparency = 1Frame.AnchorPoint = Vector2.new(0.5, 0)Frame.Position = UDim2.new(0.5, 0, 0, 0) -- mép trên giữa màn hìnhFrame.Size = UDim2.new(0, 400, 0, 40)-- TextLabel chínhlocal Label = Instance.new("TextLabel")Label.Parent = FrameLabel.Size = UDim2.new(1, 0, 1, 0)Label.BackgroundTransparency = 1Label.Font = Enum.Font.SourceSansBoldLabel.TextSize = 30Label.Text = "PHUCMAX | FPS: 0 | Ping: 0ms"Label.TextStrokeTransparency = 0 -- viền chữ rõ hơnLabel.TextStrokeColor3 = Color3.fromRGB(255,255,255) -- viền trắng cho chữ sángLabel.RichText = true-- Gradient rainbow (sáng hơn)local UIGradient = Instance.new("UIGradient")UIGradient.Parent = LabelUIGradient.Color = ColorSequence.new{    ColorSequenceKeypoint.new(0, Color3.fromRGB(255, 0, 0)),    ColorSequenceKeypoint.new(0.16, Color3.fromRGB(255, 127, 0)),    ColorSequenceKeypoint.new(0.33, Color3.fromRGB(255, 255, 0)),    ColorSequenceKeypoint.new(0.5, Color3.fromRGB(0, 255, 0)),    ColorSequenceKeypoint.new(0.66, Color3.fromRGB(0, 0, 255)),    ColorSequenceKeypoint.new(0.83, Color3.fromRGB(75, 0, 130)),    ColorSequenceKeypoint.new(1, Color3.fromRGB(148, 0, 211))}UIGradient.Rotation = 0-- Animation rainbowtask.spawn(function()    while task.wait(0.05) do        UIGradient.Rotation = (UIGradient.Rotation + 5) % 360    endend)-- FPS và Ping updatelocal RunService = game:GetService("RunService")local Stats = game:GetService("Stats")local fps, lastUpdate, frames = 0, tick(), 0RunService.RenderStepped:Connect(function()    frames = frames + 1    local now = tick()    if now - lastUpdate >= 1 then        fps = frames        frames = 0        lastUpdate = now        local ping = math.floor(Stats.Network.ServerStatsItem["Data Ping"]:GetValue())        Label.Text = string.format("PHUCMAX | FPS: %d | Ping: %dms", fps, ping)    endend)-- PHUCMAX UI PRO FIX (ANIMATION + CLIP FIX + BUTTON/TAB ANIM)local Players = game:GetService("Players")local TweenService = game:GetService("TweenService")local UserInputService = game:GetService("UserInputService")local LocalPlayer = Players.LocalPlayer-- clear oldif game.CoreGui:FindFirstChild("PHUCMAX_MAINUI") then    game.CoreGui.PHUCMAX_MAINUI:Destroy()endif game.CoreGui:FindFirstChild("PHUCMAX_TOGGLE") then    game.CoreGui.PHUCMAX_TOGGLE:Destroy()end-- Toggle button (draggable)local toggleGui = Instance.new("ScreenGui", game.CoreGui)toggleGui.Name = "PHUCMAX_TOGGLE"toggleGui.ResetOnSpawn = falselocal toggleBtn = Instance.new("ImageButton", toggleGui)toggleBtn.Name = "ToggleButton"toggleBtn.Size = UDim2.new(0,45,0,45)toggleBtn.Position = UDim2.new(0,20,0.5,-22)toggleBtn.AnchorPoint = Vector2.new(0,0.5)toggleBtn.Image = "rbxassetid://87746599009295"toggleBtn.BackgroundTransparency = 0.6toggleBtn.BorderSizePixel = 0local uicorner = Instance.new("UICorner", toggleBtn)uicorner.CornerRadius = UDim.new(0,12)local uiStroke = Instance.new("UIStroke", toggleBtn)uiStroke.Thickness = 2uiStroke.Color = Color3.fromRGB(135,206,250)-- Draggable systemlocal dragging, dragInput, dragStart, startPoslocal function update(input)	local delta = input.Position - dragStart	toggleBtn.Position = UDim2.new(		startPos.X.Scale,		startPos.X.Offset + delta.X,		startPos.Y.Scale,		startPos.Y.Offset + delta.Y	)endtoggleBtn.InputBegan:Connect(function(input)	if input.UserInputType == Enum.UserInputType.MouseButton1 or input.UserInputType == Enum.UserInputType.Touch then		dragging = true		dragStart = input.Position		startPos = toggleBtn.Position		input.Changed:Connect(function()			if input.UserInputState == Enum.UserInputState.End then dragging = false end		end)	endend)toggleBtn.InputChanged:Connect(function(input)	if input.UserInputType == Enum.UserInputType.MouseMovement or input.UserInputType == Enum.UserInputType.Touch then		dragInput = input	endend)UserInputService.InputChanged:Connect(function(input)	if input == dragInput and dragging then update(input) endend)--// UI Main Fixedlocal TweenService = game:GetService("TweenService")local UserInputService = game:GetService("UserInputService")-- ScreenGuilocal mainGui = Instance.new("ScreenGui")mainGui.Name = "PHUCMAX_MAINUI"mainGui.Parent = game.CoreGuimainGui.ResetOnSpawn = false-- Main Framelocal mainFrame = Instance.new("Frame", mainGui)mainFrame.Size = UDim2.new(0, 500, 0, 300)mainFrame.Position = UDim2.new(0.5, -250, 0.5, -150)mainFrame.BackgroundColor3 = Color3.fromRGB(40, 40, 40)mainFrame.Active = truemainFrame.Draggable = trueInstance.new("UICorner", mainFrame).CornerRadius = UDim.new(0, 10)-- Title barlocal titleBar = Instance.new("Frame", mainFrame)titleBar.Size = UDim2.new(1, 0, 0, 30)titleBar.BackgroundColor3 = Color3.fromRGB(30, 30, 30)Instance.new("UICorner", titleBar).CornerRadius = UDim.new(0, 10)local title = Instance.new("TextLabel", titleBar)title.Size = UDim2.new(1, -60, 1, 0)title.Position = UDim2.new(0, 10, 0, 0)title.BackgroundTransparency = 1title.Text = "PHUCMAX HUB"title.Font = Enum.Font.GothamBoldtitle.TextSize = 18title.TextColor3 = Color3.fromRGB(255,255,255)title.TextXAlignment = Enum.TextXAlignment.Left-- Minimize & Close buttonslocal closeBtn = Instance.new("TextButton", titleBar)closeBtn.Size = UDim2.new(0, 30, 1, 0)closeBtn.Position = UDim2.new(1, -35, 0, 0)closeBtn.Text = "×"closeBtn.TextColor3 = Color3.fromRGB(255,0,0)closeBtn.Font = Enum.Font.GothamBoldcloseBtn.TextSize = 18closeBtn.BackgroundTransparency = 1local miniBtn = Instance.new("TextButton", titleBar)miniBtn.Size = UDim2.new(0, 30, 1, 0)miniBtn.Position = UDim2.new(1, -70, 0, 0)miniBtn.Text = "-"miniBtn.TextColor3 = Color3.fromRGB(255,255,0)miniBtn.Font = Enum.Font.GothamBoldminiBtn.TextSize = 18miniBtn.BackgroundTransparency = 1closeBtn.MouseButton1Click:Connect(function()    mainGui:Destroy()end)miniBtn.MouseButton1Click:Connect(function()    mainFrame.Visible = not mainFrame.Visibleend)-- TAB Arealocal tabFrame = Instance.new("ScrollingFrame", mainFrame)tabFrame.Name = "TabFrame"tabFrame.Size = UDim2.new(1, -20, 0, 40)tabFrame.Position = UDim2.new(0,10,0,40)tabFrame.BackgroundTransparency = 1tabFrame.ScrollBarThickness = 6tabFrame.ClipsDescendants = truetabFrame.AutomaticCanvasSize = Enum.AutomaticSize.XtabFrame.ScrollingDirection = Enum.ScrollingDirection.Xlocal tabList = Instance.new("UIListLayout", tabFrame)tabList.FillDirection = Enum.FillDirection.HorizontaltabList.SortOrder = Enum.SortOrder.LayoutOrdertabList.Padding = UDim.new(0,10)-- Notify Systemlocal function notify(msg)    local note = Instance.new("TextLabel", mainGui)    note.Size = UDim2.new(0,250,0,40)    note.Position = UDim2.new(1,-260,1,-60)    note.BackgroundColor3 = Color3.fromRGB(0,50,150)     note.BackgroundTransparency = 0.2    note.Text = msg    note.Font = Enum.Font.GothamBold    note.TextSize = 16    note.TextColor3 = Color3.fromRGB(255,255,255)    note.AnchorPoint = Vector2.new(0,0)    local c = Instance.new("UICorner", note)    c.CornerRadius = UDim.new(0,8)    local s = Instance.new("UIStroke", note)    s.Thickness = 1.5    s.Color = Color3.fromRGB(135,206,250)    task.delay(3,function() note:Destroy() end)end-- Tab managementlocal tabs = {}local currentTab = nillocal function createTab(name)    local tabBtn = Instance.new("TextButton", tabFrame)    tabBtn.Size = UDim2.new(0,120,1,0)    tabBtn.Text = name    tabBtn.Font = Enum.Font.GothamBold    tabBtn.TextSize = 16    tabBtn.TextColor3 = Color3.fromRGB(0,0,0)    tabBtn.BackgroundColor3 = Color3.fromRGB(255,255,255)    tabBtn.BackgroundTransparency = 0.35    tabBtn.AutoButtonColor = false    Instance.new("UICorner", tabBtn).CornerRadius = UDim.new(0,8)    local stroke = Instance.new("UIStroke", tabBtn)    stroke.Thickness = 1.5    stroke.Color = Color3.fromRGB(180,220,255)    stroke.Transparency = 0.3    local thisContent = Instance.new("ScrollingFrame", mainFrame)    thisContent.Name = name .. "_Content"    thisContent.Size = UDim2.new(1, -20, 1, -90)    thisContent.Position = UDim2.new(0,10,0,80)    thisContent.BackgroundTransparency = 1    thisContent.ScrollBarThickness = 8    thisContent.ClipsDescendants = true    thisContent.AutomaticCanvasSize = Enum.AutomaticSize.Y    thisContent.ScrollingDirection = Enum.ScrollingDirection.Y    thisContent.Visible = false    local list = Instance.new("UIListLayout", thisContent)    list.SortOrder = Enum.SortOrder.LayoutOrder    list.Padding = UDim.new(0,10)    list.HorizontalAlignment = Enum.HorizontalAlignment.Center    tabs[name] = {btn = tabBtn, frame = thisContent}    tabBtn.MouseButton1Click:Connect(function()        for n, data in pairs(tabs) do            data.frame.Visible = false            data.btn.BackgroundTransparency = 0.35        end        thisContent.Visible = true        tabBtn.BackgroundTransparency = 0        currentTab = name        local t1 = TweenService:Create(tabBtn, TweenInfo.new(0.08), {Size = UDim2.new(0,115,1,0)})        local t2 = TweenService:Create(tabBtn, TweenInfo.new(0.1), {Size = UDim2.new(0,120,1,0)})        t1:Play()        t1.Completed:Connect(function() t2:Play() end)    end)    return thisContentend-- Helperslocal function createButton(parent, text, callback)    local btn = Instance.new("TextButton", parent)    btn.Size = UDim2.new(0.9, 0, 0, 40)    btn.BackgroundColor3 = Color3.fromRGB(255,255,255)    btn.BackgroundTransparency = 0.35    btn.Text = text    btn.Font = Enum.Font.GothamBold    btn.TextSize = 16    btn.TextColor3 = Color3.fromRGB(0,0,0)    Instance.new("UICorner", btn).CornerRadius = UDim.new(0,8)    btn.MouseButton1Click:Connect(function()        if callback then callback() end    end)endlocal function createToggle(parent, text, default, callback)    local frame = Instance.new("TextButton", parent)    frame.Size = UDim2.new(0.9, 0, 0, 40)    frame.BackgroundColor3 = Color3.fromRGB(255,255,255)    frame.BackgroundTransparency = 0.35    frame.Text = text .. " [".. (default and "ON" or "OFF") .. "]"    frame.Font = Enum.Font.GothamBold    frame.TextSize = 16    frame.TextColor3 = Color3.fromRGB(0,0,0)    Instance.new("UICorner", frame).CornerRadius = UDim.new(0,8)    local state = default    frame.MouseButton1Click:Connect(function()        state = not state        frame.Text = text .. " [".. (state and "ON" or "OFF") .. "]"        if callback then callback(state) end    end)end-- DEMOlocal tab1 = createTab("Farm")createButton(tab1, "Auto Farm", function() notify("Auto Farm started!") end)createToggle(tab1, "Auto Skill", false, function(state) notify("Skill: "..tostring(state)) end)local tab2 = createTab("Fix Lag")createButton(tab2, "Remove Trees", function() notify("Trees removed") end)createButton(tab2, "Remove Shadows", function() notify("Shadows removed") end)-- Default show first tabtabs["Farm"].frame.Visible = true