--// UI PHUCMAX + FPS + Ping (Rainbow Gradient rõ sáng)local ScreenGui = Instance.new("ScreenGui")ScreenGui.Name = "PHUCMAX_UI"ScreenGui.ResetOnSpawn = falseScreenGui.Parent = game.CoreGui-- Frame chứa textlocal Frame = Instance.new("Frame")Frame.Parent = ScreenGuiFrame.BackgroundTransparency = 1Frame.AnchorPoint = Vector2.new(0.5, 0)Frame.Position = UDim2.new(0.5, 0, 0, 0)Frame.Size = UDim2.new(0, 400, 0, 40)-- TextLabel chínhlocal Label = Instance.new("TextLabel")Label.Parent = FrameLabel.Size = UDim2.new(1, 0, 1, 0)Label.BackgroundTransparency = 1Label.Font = Enum.Font.SourceSansBoldLabel.TextSize = 30Label.Text = "PHUCMAX | FPS: 0 | Ping: 0ms"Label.TextStrokeTransparency = 0Label.TextStrokeColor3 = Color3.fromRGB(255,255,255)Label.RichText = true-- Gradient rainbow (sáng hơn)local UIGradient = Instance.new("UIGradient")UIGradient.Parent = LabelUIGradient.Color = ColorSequence.new{    ColorSequenceKeypoint.new(0, Color3.fromRGB(255, 0, 0)),    ColorSequenceKeypoint.new(0.16, Color3.fromRGB(255, 127, 0)),    ColorSequenceKeypoint.new(0.33, Color3.fromRGB(255, 255, 0)),    ColorSequenceKeypoint.new(0.5, Color3.fromRGB(0, 255, 0)),    ColorSequenceKeypoint.new(0.66, Color3.fromRGB(0, 0, 255)),    ColorSequenceKeypoint.new(0.83, Color3.fromRGB(75, 0, 130)),    ColorSequenceKeypoint.new(1, Color3.fromRGB(148, 0, 211))}UIGradient.Rotation = 0task.spawn(function()    while task.wait(0.05) do        UIGradient.Rotation = (UIGradient.Rotation + 5) % 360    endend)local RunService = game:GetService("RunService")local Stats = game:GetService("Stats")local fps, lastUpdate, frames = 0, tick(), 0RunService.RenderStepped:Connect(function()    frames = frames + 1    local now = tick()    if now - lastUpdate >= 1 then        fps = frames        frames = 0        lastUpdate = now        local ping = math.floor(Stats.Network.ServerStatsItem["Data Ping"]:GetValue())        Label.Text = string.format("PHUCMAX | FPS: %d | Ping: %dms", fps, ping)    endend)local Players = game:GetService("Players")local TweenService = game:GetService("TweenService")local UserInputService = game:GetService("UserInputService")local LocalPlayer = Players.LocalPlayerif game.CoreGui:FindFirstChild("PHUCMAX_MAINUI") then    game.CoreGui.PHUCMAX_MAINUI:Destroy()endif game.CoreGui:FindFirstChild("PHUCMAX_TOGGLE") then    game.CoreGui.PHUCMAX_TOGGLE:Destroy()end-- Toggle button (draggable)local toggleGui = Instance.new("ScreenGui", game.CoreGui)toggleGui.Name = "PHUCMAX_TOGGLE"toggleGui.ResetOnSpawn = falselocal toggleBtn = Instance.new("ImageButton", toggleGui)toggleBtn.Name = "ToggleButton"toggleBtn.Size = UDim2.new(0,54,0,54)toggleBtn.Position = UDim2.new(0,20,0.5,-27)toggleBtn.AnchorPoint = Vector2.new(0,0.5)toggleBtn.Image = "rbxassetid://70869581156112"toggleBtn.BackgroundTransparency = 0.6toggleBtn.BorderSizePixel = 0local uicorner = Instance.new("UICorner", toggleBtn)uicorner.CornerRadius = UDim.new(0,16)local uiStroke = Instance.new("UIStroke", toggleBtn)uiStroke.Thickness = 2uiStroke.Color = Color3.fromRGB(135,206,250)-- Draggable system (fix nhấn vùng rộng hơn)local dragging, dragInput, dragStart, startPoslocal function update(input)	local delta = input.Position - dragStart	toggleBtn.Position = UDim2.new(		startPos.X.Scale,		startPos.X.Offset + delta.X,		startPos.Y.Scale,		startPos.Y.Offset + delta.Y	)endtoggleBtn.InputBegan:Connect(function(input)	if input.UserInputType == Enum.UserInputType.MouseButton1 or input.UserInputType == Enum.UserInputType.Touch then		dragging = true		dragStart = input.Position		startPos = toggleBtn.Position		input.Changed:Connect(function()			if input.UserInputState == Enum.UserInputState.End then dragging = false end		end)	endend)toggleBtn.InputChanged:Connect(function(input)	if input.UserInputType == Enum.UserInputType.MouseMovement or input.UserInputType == Enum.UserInputType.Touch then		dragInput = input	endend)UserInputService.InputChanged:Connect(function(input)	if input == dragInput and dragging then update(input) endend)-- MAIN UIlocal mainGui = Instance.new("ScreenGui", game.CoreGui)mainGui.Name = "PHUCMAX_MAINUI"mainGui.ResetOnSpawn = falsemainGui.Enabled = falselocal mainFrame = Instance.new("ImageLabel", mainGui)mainFrame.Name = "MainFrame"mainFrame.Size = UDim2.new(0,400,0,250)mainFrame.Position = UDim2.new(0.5,0,0.5,0)mainFrame.AnchorPoint = Vector2.new(0.5,0.5)mainFrame.Image = "rbxassetid://87746599009295"mainFrame.BackgroundTransparency = 1mainFrame.ScaleType = Enum.ScaleType.CropmainFrame.ClipsDescendants = truelocal mainCorner = Instance.new("UICorner", mainFrame)mainCorner.CornerRadius = UDim.new(0,20)local mainStroke = Instance.new("UIStroke", mainFrame)mainStroke.Thickness = 3mainStroke.Color = Color3.fromRGB(180,220,255)-- Drag function cho mainFrame (drag vùng rộng hơn)local dragging_main, dragInput_main, dragStart_main, startPos_mainlocal function update_main(input)    local delta = input.Position - dragStart_main    mainFrame.Position = UDim2.new(        startPos_main.X.Scale, startPos_main.X.Offset + delta.X,        startPos_main.Y.Scale, startPos_main.Y.Offset + delta.Y    )endmainFrame.InputBegan:Connect(function(input)    if input.UserInputType == Enum.UserInputType.MouseButton1 or input.UserInputType == Enum.UserInputType.Touch then        dragging_main = true        dragStart_main = input.Position        startPos_main = mainFrame.Position        input.Changed:Connect(function()            if input.UserInputState == Enum.UserInputState.End then                dragging_main = false            end        end)    endend)mainFrame.InputChanged:Connect(function(input)    if input.UserInputType == Enum.UserInputType.MouseMovement or input.UserInputType == Enum.UserInputType.Touch then        dragInput_main = input    endend)UserInputService.InputChanged:Connect(function(input)    if input == dragInput_main and dragging_main then        update_main(input)    endend)-- Toggle Main UI on clicktoggleBtn.MouseButton1Click:Connect(function()    mainGui.Enabled = not mainGui.Enabledend)-- TAB area (icon + tên, rõ ràng, không mờ)local tabFrame = Instance.new("ScrollingFrame", mainFrame)tabFrame.Name = "TabFrame"tabFrame.Size = UDim2.new(1, -20, 0, 48)tabFrame.Position = UDim2.new(0,10,0,10)tabFrame.BackgroundTransparency = 1tabFrame.ScrollBarThickness = 6tabFrame.ClipsDescendants = truetabFrame.AutomaticCanvasSize = Enum.AutomaticSize.XtabFrame.ScrollingDirection = Enum.ScrollingDirection.XtabFrame.ZIndex = 2local tabList = Instance.new("UIListLayout", tabFrame)tabList.FillDirection = Enum.FillDirection.HorizontaltabList.SortOrder = Enum.SortOrder.LayoutOrdertabList.Padding = UDim.new(0,7)-- NOTIFY system góc phải dướilocal function notify(msg)    local note = Instance.new("TextLabel", mainGui)    note.Size = UDim2.new(0,250,0,40)    note.Position = UDim2.new(1,-260,1,-60)    note.BackgroundColor3 = Color3.fromRGB(0,50,150)    note.BackgroundTransparency = 0.2    note.Text = msg    note.Font = Enum.Font.GothamBold    note.TextSize = 16    note.TextColor3 = Color3.fromRGB(255,255,255)    note.AnchorPoint = Vector2.new(0,0)    local c = Instance.new("UICorner", note)    c.CornerRadius = UDim.new(0,8)    local s = Instance.new("UIStroke", note)    s.Thickness = 1.5    s.Color = Color3.fromRGB(135,206,250)    task.delay(3,function() note:Destroy() end)end-- Tab quản lýlocal tabs = {}-- Helper: create tab with icon + tênlocal function createTab(name, icon)    local tabBtn = Instance.new("TextButton", tabFrame)    tabBtn.Size = UDim2.new(0,130,1,0)    tabBtn.Text = ""    tabBtn.BackgroundColor3 = Color3.fromRGB(255,255,255)    tabBtn.BackgroundTransparency = 0.15    tabBtn.Font = Enum.Font.GothamBold    tabBtn.TextSize = 16    tabBtn.AutoButtonColor = false    tabBtn.ClipsDescendants = true    Instance.new("UICorner", tabBtn).CornerRadius = UDim.new(0,13)    local stroke = Instance.new("UIStroke", tabBtn)    stroke.Thickness = 2    stroke.Color = Color3.fromRGB(180,220,255)    stroke.Transparency = 0.08    -- Icon nếu có    if icon then        local iconLabel = Instance.new("ImageLabel", tabBtn)        iconLabel.Size = UDim2.new(0,26,0,26)        iconLabel.Position = UDim2.new(0,10,0.5,-13)        iconLabel.BackgroundTransparency = 1        iconLabel.Image = icon        iconLabel.Name = "TabIcon"    end    -- Tên tab    local txt = Instance.new("TextLabel", tabBtn)    txt.Size = UDim2.new(1, icon and -38 or -10, 1, 0)    txt.Position = icon and UDim2.new(0,40,0,0) or UDim2.new(0,8,0,0)    txt.BackgroundTransparency = 1    txt.Text = name    txt.Font = Enum.Font.GothamBold    txt.TextScaled = true    txt.TextWrapped = true    txt.TextColor3 = Color3.fromRGB(35,54,94)    txt.TextXAlignment = Enum.TextXAlignment.Left    txt.Name = "TabName"    txt.ZIndex = 2    -- Content    local thisContent = Instance.new("ScrollingFrame", mainFrame)    thisContent.Name = name .. "_Content"    thisContent.Size = UDim2.new(1, -20, 1, -70)    thisContent.Position = UDim2.new(0,10,0,58)    thisContent.BackgroundTransparency = 1    thisContent.ScrollBarThickness = 8    thisContent.ClipsDescendants = true    thisContent.AutomaticCanvasSize = Enum.AutomaticSize.Y    thisContent.ScrollingDirection = Enum.ScrollingDirection.Y    thisContent.Visible = false    thisContent.ZIndex = 1    local list = Instance.new("UIListLayout", thisContent)    list.SortOrder = Enum.SortOrder.LayoutOrder    list.Padding = UDim.new(0,10)    list.HorizontalAlignment = Enum.HorizontalAlignment.Center    tabs[name] = {btn = tabBtn, frame = thisContent}    tabBtn.MouseButton1Click:Connect(function()        for n, data in pairs(tabs) do            data.frame.Visible = false            data.btn.BackgroundTransparency = 0.15        end        thisContent.Visible = true        tabBtn.BackgroundTransparency = 0        local t1 = TweenService:Create(tabBtn, TweenInfo.new(0.09), {Size = UDim2.new(0,120,1,0)})        local t2 = TweenService:Create(tabBtn, TweenInfo.new(0.12), {Size = UDim2.new(0,130,1,0)})        t1:Play() t1.Completed:Connect(function() t2:Play() end)    end)    -- Tab đầu tiên tự bật    if #tabFrame:GetChildren() <= 3 then        thisContent.Visible = true        tabBtn.BackgroundTransparency = 0    end    return thisContentend-- Helper: Nút chuẩn luxury (fix rõ text, không còn mờ)local function luxuryButton(parent, text, color, callback, icon)    local btn = Instance.new("TextButton", parent)    btn.Size = UDim2.new(1, -10, 0, 46)    btn.Text = ""    btn.BackgroundColor3 = color or Color3.fromRGB(255,255,255)    btn.BackgroundTransparency = 0.15    btn.AutoButtonColor = false    local uic = Instance.new("UICorner", btn)    uic.CornerRadius = UDim.new(0,13)    local stroke = Instance.new("UIStroke", btn)    stroke.Thickness = 2    stroke.Color = Color3.fromRGB(135,206,250)    stroke.Transparency = 0.08    -- Icon nếu có    local iconLabel    if icon then        iconLabel = Instance.new("ImageLabel", btn)        iconLabel.Size = UDim2.new(0,32,0,32)        iconLabel.Position = UDim2.new(0,10,0.5,-16)        iconLabel.BackgroundTransparency = 1        iconLabel.Image = icon    end    -- Text    local txt = Instance.new("TextLabel", btn)    txt.Size = UDim2.new(1, icon and -56 or -18, 1, 0)    txt.Position = icon and UDim2.new(0,48,0,0) or UDim2.new(0,8,0,0)    txt.BackgroundTransparency = 1    txt.Text = text    txt.Font = Enum.Font.GothamBold    txt.TextScaled = true    txt.TextWrapped = true    txt.TextColor3 = Color3.fromRGB(35,54,94)    txt.TextXAlignment = Enum.TextXAlignment.Left    btn.ClipsDescendants = true    btn.MouseButton1Click:Connect(function()        local t1 = TweenService:Create(btn, TweenInfo.new(0.09), {BackgroundTransparency = 0.3, Size = UDim2.new(1,-20,0,42)})        local t2 = TweenService:Create(btn, TweenInfo.new(0.12), {BackgroundTransparency = 0.15, Size = UDim2.new(1,-10,0,46)})        t1:Play() t1.Completed:Connect(function() t2:Play() end)        if callback then callback() end    end)    return btnend-- Helper: Luxury Toggle (đã fix rõ chữ, vùng bấm chuẩn)local function luxuryToggle(parent, text, default, callback)    local toggleFrame = Instance.new("Frame", parent)    toggleFrame.Size = UDim2.new(1, -10, 0, 46)    toggleFrame.BackgroundTransparency = 1    local btn = Instance.new("TextButton", toggleFrame)    btn.Size = UDim2.new(1,-54,1,0)    btn.Position = UDim2.new(0,0,0,0)    btn.BackgroundColor3 = Color3.fromRGB(255,255,255)    btn.BackgroundTransparency = 0.15    btn.Text = ""    btn.AutoButtonColor = false    Instance.new("UICorner", btn).CornerRadius = UDim.new(0,13)    local stroke = Instance.new("UIStroke", btn)    stroke.Thickness = 2    stroke.Color = Color3.fromRGB(135,206,250)    stroke.Transparency = 0.08    local txt = Instance.new("TextLabel", btn)    txt.Size = UDim2.new(1,0,1,0)    txt.BackgroundTransparency = 1    txt.Text = text    txt.Font = Enum.Font.GothamBold    txt.TextScaled = true    txt.TextWrapped = true    txt.TextColor3 = Color3.fromRGB(35,54,94)    txt.TextXAlignment = Enum.TextXAlignment.Left    txt.Position = UDim2.new(0,12,0,0)    local tog = Instance.new("ImageButton", toggleFrame)    tog.Size = UDim2.new(0,38,0,38)    tog.Position = UDim2.new(1,-38,0.5,-19)    tog.BackgroundTransparency = 1    tog.Image = default and "rbxassetid://6031068421" or "rbxassetid://6031068420"    tog.AnchorPoint = Vector2.new(1,0.5)    tog.ClipsDescendants = true    local state = default    tog.MouseButton1Click:Connect(function()        state = not state        tog.Image = state and "rbxassetid://6031068421" or "rbxassetid://6031068420"        if callback then callback(state) end    end)    return toggleFrameend-- Helper: Luxury Slider, Dropdown... (không đổi, đã chuẩn)-- ... [phần luxurySlider và luxuryDropdown giữ nguyên như bản bạn gửi]-- Tạo tab (icon + tên, rõ, chỉ cần sửa icon nếu muốn)local infoContent = createTab("Info", "rbxassetid://6034996695")luxuryButton(infoContent, "PHUCMAX Luxury Button", Color3.fromRGB(255,255,255), function()    notify("Luxury button đã click!")end, "rbxassetid://6034996695")luxuryButton(infoContent, "Copy Discord", Color3.fromRGB(220,246,255), function()    setclipboard("https://discord.gg/a7rtPheBY6")    notify("Đã copy Discord!")end, "rbxassetid://6031071050")luxuryToggle(infoContent, "Bật/tắt chế độ Luxury", false, function(state)    notify("Trạng thái luxury: "..(state and "ON" or "OFF"))end)luxurySlider(infoContent, "Điều chỉnh độ sáng", 10, 100, 50, function(val)    -- Tùy code dùng valend)local _, dropFrame = luxuryDropdown(infoContent, "Chọn chế độ", {"Mode 1", "Mode 2", "Mode 3"}, function(val)    notify("Đã chọn: "..val)end)dropFrame.Parent = infoContentlocal tabSetting = createTab("Setting", "rbxassetid://87746599009295")luxuryButton(tabSetting, "Nút Setting", Color3.fromRGB(232,255,248), function()    notify("Setting Clicked")end, "rbxassetid://87746599009295")luxuryToggle(tabSetting, "Auto Save", true, function(state)    notify("Auto Save: " .. tostring(state))end)luxurySlider(tabSetting, "Volume", 0, 100, 30, function(val)    -- Nothingend)local demoContent = createTab("Demo", "rbxassetid://6034996695")luxuryButton(demoContent, "Luxury Button (White)", Color3.fromRGB(255,255,255), function()    notify("Bạn đã bấm Luxury Button!")end, "rbxassetid://87746599009295")luxuryButton(demoContent, "Luxury Button (Xanh Sky)", Color3.fromRGB(210,246,255), function()    notify("Luxury Xanh Sky!")end, "rbxassetid://87746599009295")luxuryButton(demoContent, "Luxury Button (Xanh lá)", Color3.fromRGB(224,255,232), function()    notify("Luxury Xanh Lá!")end, "rbxassetid://87746599009295")luxuryToggle(demoContent, "Bật/Tắt Luxury Toggle", false, function(state)    notify("Luxury Toggle: "..(state and "Đang bật" or "Đang tắt"))end)luxuryToggle(demoContent, "Auto Mode", true, function(state)    notify("Auto Mode: "..(state and "ON" or "OFF"))end)luxurySlider(demoContent, "Độ sáng màn hình", 0, 100, 50, function(val)    notify("Độ sáng: "..val)end)luxurySlider(demoContent, "Âm lượng", 0, 10, 5, function(val)    notify("Âm lượng: "..val)end)local _, dropFrame1 = luxuryDropdown(demoContent, "Chọn màu sắc", {"Đỏ", "Lục", "Lam", "Vàng"}, function(val)    notify("Bạn chọn: "..val)end)dropFrame1.Parent = demoContentlocal _, dropFrame2 = luxuryDropdown(demoContent, "Chọn chế độ", {"Chuyên nghiệp", "Bình thường", "Cơ bản"}, function(val)    notify("Chế độ: "..val)end)dropFrame2.Parent = demoContent-- KẾT THÚC FILE (KHÔNG lặp lại đoạn tạo tab phía dưới!)